mapper.select:
select 

from uas_link_friend_apply
where userid = #{userId,jdbcType=BIGINT}
  and friendid = #{friendId,jdbcType=BIGINT}

mapper.delete:
delete from uas_link_friend_apply
where userid = #{userid,jdbcType=BIGINT}
  and friendid = #{friendid,jdbcType=BIGINT}
  and txid = #{txid,jdbcType=BIGINT}

mapper.update:
update uas_link_friend_apply
set status = #{status,jdbcType=SMALLINT},
  applynote = #{applyNote,jdbcType=VARCHAR},
  applytime = #{applyTime,jdbcType=TIMESTAMP},
  checktime = #{checkTime,jdbcType=TIMESTAMP},
  checknote = #{checkNote,jdbcType=VARCHAR}
where userid = #{userId,jdbcType=BIGINT}
  and friendid = #{friendId,jdbcType=BIGINT}
  and txid = #{txId,jdbcType=BIGINT}
