mapper.select:
select 

from uas_link_friend
where userid = #{userId,jdbcType=BIGINT}
  and friendid = #{friendId,jdbcType=BIGINT}

mapper.delete:
delete from uas_link_friend
where userid = #{userId,jdbcType=BIGINT}
  and friendid = #{friendId,jdbcType=BIGINT}

mapper.update:
update uas_link_friend


    notename = #{noteName,jdbcType=VARCHAR},


    notes = #{notes,jdbcType=VARCHAR},


    labels = #{labels,jdbcType=VARCHAR},


where userid = #{userId,jdbcType=BIGINT}
  and friendid = #{friendId,jdbcType=BIGINT}

update uas_link_friend
set notename = #{noteName,jdbcType=VARCHAR},
  notes = #{notes,jdbcType=VARCHAR},
  labels = #{labels,jdbcType=VARCHAR}
where userid = #{userId,jdbcType=BIGINT}
  and friendid = #{friendId,jdbcType=BIGINT}

mapper.delete:
delete
from uas_link_friend
where  userid = #{userId,jdbcType=BIGINT}
